var games_div=document.getElementById("endless-games-list");var load_type=games_div.dataset.type;var load_key=games_div.dataset.key;var category_id=games_div.dataset.id;var games_start_position=50;var games_to_load=50;var request_status="ready";function isInViewport(a){var b=a.getBoundingClientRect();if(b.top>=0&&b.left>=0&&Math.floor(b.bottom-140)<=window.innerHeight){return true}else{return false}}function check(){if(request_status==="ready"){if(isInViewport(document.getElementById("below-games"))){loadGames()}}}function loadGames(){var a=new XMLHttpRequest();a.onreadystatechange=function(){if(this.readyState==4&&this.status==200){if(this.responseText===""){request_status="no-more-games"}else{games_div.insertAdjacentHTML("beforeend",this.responseText);request_status="ready"}}};if(load_type==="category"){a.open("https://www.silvergames.com/en/GET","https://www.silvergames.com/div/load-games.php?c="+load_key+"&l="+document.documentElement.lang+"&s="+games_start_position,true)}else{if(load_type==="tag"){a.open("https://www.silvergames.com/en/GET","https://www.silvergames.com/div/load-games.php?t="+load_key+"&l="+document.documentElement.lang+"&s="+games_start_position,true)}}a.send();request_status="isloading";games_start_position=games_start_position+games_to_load}document.addEventListener("scroll",check);